from openai import OpenAI
import pandas as pd


client = OpenAI()


def get_embeddings(texts, model="text-embedding-3-small"):
    # 각 텍스트에 대한 임베딩을 저장할 리스트
    RAGs = []
    
    for text in texts:
        # API를 사용하여 텍스트의 임베딩 생성
        response = client.embeddings.create(
            input=[text],
            model=model
        )
        # 생성된 임베딩을 리스트에 추가
        RAGs.append(response.data[0].embedding)
    
    return RAGs

